import React, { Component } from 'react';
import Card from './Card';

class Cards extends Component {
  
    mapCards(){
        if (this.props.cards){
            return(
                <div>{ this.props.cards.map(card => <Card name={card.name} handle={card.handle} location={card.location} following={card.following} followers={card.followers} repos={card.repos} />)}
      </div>
            )
        }
    }
    
    render() {
    return(<div>
      {this.mapCards()}
            </div>
            );
  }
}

export default Cards;
