import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Search from './Search';

class Card extends Component {
    state = {
        data:[]
        
    }

    componentDidMount() {
        fetch(this.props.url)
        .then(res=>res.json())
        .then(data=>this.setState({data:data.items}))
    }

  render() {
    return( 
        <div className="col-4 card">
            <div className="card-body">
                <h5 className="card-title">{this.state.data.name}</h5>
                <p className="card-text">{this.state.data.login}</p>
                <p className="card-text">{this.state.data.location}</p>
                <p className="card-text">Followers: {this.state.data.followers}, Following: {this.state.data.following}, Repos: {this.state.data.public_repos},</p>
            </div>
        </div>
                
        
    );
  }
}

export default Card;
