import React, { Component } from 'react';
import Card from './Card';

class Cards extends Component {
    state = {
        name:""
    }
  
    getName(name){
        var user;
        fetch("https://api.github.com/users/"+name)
        .then(res=>res.json())
        .then(data=>this.setState({userData:data.name}));
        
        return this.state.name
    }
    
    mapCards(){
        if (this.props.cards){
            return(
                <div>{ this.props.cards.map(card => <Card name={this.getName(card.name)} handle={card.login} location={card.location} following={card.following} followers={card.followers} repos={card.repos} />)}
      </div>
            )
        }
    }
    
    render() {
    return(<div>
      {this.mapCards()}
            </div>
            );
  }
}

export default Cards;
